Material.Shader:


varying vec3 positionWorld;
varying vec3 color;

void main(void) 
{
  color = gl_Color.rgb;
  positionWorld = vec3(gl_Vertex);
  gl_Position = ftransform();
}


uniform vec4 plane1;
uniform float fiberThickness;

varying vec3 positionWorld;
varying vec3 color;

void main(void)
{
   float r1 = dot(positionWorld, plane1.xyz) - plane1.w;

    if ( abs(r1) > fiberThickness )
      discard;

   float x = (r1+fiberThickness)/4.0;

   x = 1.0 - x;

   gl_FragColor = vec4(color*x, 1.0); 
}
